rce src="<?php echo esc_url($vedio_url);?>" type="video/webm" />			  <source src="<?php echo esc_url($vedio_url);?>" type="video/mp4" />			  <source src="<?php echo esc_url($vedio_url);?>" type="video/ogg" />			 </video>                 <?php                 else:                     $outputEmbed =  preg_replace(	"/\s*[a-zA-Z\/\/:\.]*youtu(be.com\/watch\?v=|.be\/)([a-zA-Z0-9\-_]+)([a-zA-Z0-9\/\*\-\_\?\&\;\%\=\.]*)/i","$2",$vedio_url_yt);                     echo '					 <iframe poster="'.$video_banner_img.' id="lp-youtube-banner" frameborder="0" width="100%" height="100%" src="https://www.youtube.com/embed/'.$outputEmbed.'?autoplay=1&loop=1&controls=0&mute=1" frameborder="0" allow="autoplay; encrypted-media" allowfullscreen></iframe>					 ';                 endif;                 ?>			 </div>		   <?php		}	}    ?>    <div class="lp-header-search lp-header-search-sidebar-style">        <?php        $top_title  =   $listingpro_options['top_main_title'];        $main_text_des  =   $listingpro_options['main_text'];        $banner_logo    =   $listingpro_options['banner_logo_search2'];        ?>        <div class="container">            <div class="row">				<div class="col-md-7">					<?php					if( is_front_page() ):					?>				   					<?php						$locationType = 'withip';					if( isset( $top_title ) && $top_title != '' && isset( $main_text_des ) && $main_text_des != '' ):					?>					<div class="col-md-12 lp_auto_loc_container">						<div class="lp-header-search-tagline lp-header-search-tagline-sidebar-banner" data-locnmethod="<?php echo esc_attr($locationType); ?>"><?php echo $top_title; ?></div>						 <div class="sidebar-banner-des" data-locnmethod="<?php echo esc_attr($locationType); ?>"><?php echo $main_text_des; ?></div>					</div>					<?php endif; ?>										<?php					$home_banner_taxonomy   =   $listingpro_options['home_banner_taxonomy'];					$banner_tax =   'home_banner_cats';					$banner_tax_slug    =   'listing-category';